mapper.select:
SELECT total_point
FROM total_point
WHERE id = #{param1}

SELECT count(0)
FROM user_point_detail
WHERE user_id = #{param1}
  AND create_time BETWEEN #{param2} AND #{param3}
  AND type = #{param4}

mapper.update:
UPDATE total_point
SET total_point = total_point + #{param2}
WHERE id = #{param1}
